#[derive(Default)]
struct Hand {
    score: i32,
}

#[derive(PartialEq, Eq)]
enum Outcome {
    Unknown,
    Loss,
    Draw,
    Win
}

struct Round {
    hand: Hand,
    outcome: Outcome
}

// impl Ord for Hand {
//     fn cmp(&self, other: &Self) -> Ordering {

//     }
// }

fn score_round(round: &(Hand, Hand)) -> i32 {
    match round.1.score {
        1 => match round.0.score {
            1 => return 3,
            2 => return 0,
            3 => return 6,
            _ => return 0,
        },
        2 => match round.0.score {
            1 => return 6,
            2 => return 3,
            3 => return 0,
            _ => return 0,
        },
        3 => match round.0.score {
            1 => return 0,
            2 => return 6,
            3 => return 3,
            _ => return 0,
        },
        _ => return 0,
    }
}

fn determine_hand_score(round: &Round) -> Result<i32, &'static str> {
    return match &round.outcome {
        Outcome::Win => match round.hand.score {
            1 => Ok(2),
            2 => Ok(3),
            3 => Ok(1),
            _ => Err("Invalid hand")
        },
        Outcome::Draw => match round.hand.score {
            1 => Ok(1),
            2 => Ok(2),
            3 => Ok(3),
            _ => Err("Invalid hand")
        },
        Outcome::Loss => match round.hand.score {
            1 => Ok(3),
            2 => Ok(1),
            3 => Ok(2),
            _ => Err("Invalid hand")
        },
        _ => Err("Cannot calculate based on unknown outcome")
    }
}

